(92, 'Sunset Heights Open Space', 'Location: Along Sunset Heights', 103.768867997661, 1.32794501705898, 0),
(93, 'Almond Crescent Playground', 'Location: Junction of Almond Crescent and Almond Street', 103.771884003432, 1.37321500854805, 0),
